      it "re-renders the 'new' template" do
        post :create, {:grocery_list_item => invalid_attributes}, valid_session
        expect(response).to render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      let(:new_attributes) {
        { name: 'Updated', grocery_list_id: @grocery_list.id }
