    describe "with invalid params" do
      it "assigns the grocery_list_item as @grocery_list_item" do
        grocery_list_item = GroceryListItem.create! valid_attributes
        put :update, {:id => grocery_list_item.to_param, :grocery_list_item => invalid_attributes}, valid_session
        expect(assigns(:grocery_list_item)).to eq(grocery_list_item)
